
import java.util.Scanner;

public class Addition2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int A = s.nextInt();
		int B = s.nextInt();
		int C = s.nextInt();
		int D = A + B + C;
		System.out.println("Sum of " + A + " and " + B + " and " + C + " is = " + D);
	}
}
