
public class Addplusplus {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=5,b;
b=a++;
System.out.println("Value of a:"+ a +" Value of b:"+b);

b=++a;
System.out.println("Value of a:"+ a +" Value of b:"+b);

b=a--;
System.out.println("Value of a:"+ a +" Value of b:"+b);

b=--a;
System.out.println("Value of a:"+ a +" Value of b:"+b);
	}

}
